import java.io.IOException;
import java.io.PrintWriter;
import javax.servlet.ServletException;
import javax.servlet.annotation.WebServlet;
import javax.servlet.http.HttpServlet;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;

@WebServlet("/lifecycle")
public class LifecycleDemoServlet extends HttpServlet {

    @Override
    public void init() throws ServletException {
        // Called once when the servlet is first loaded
        System.out.println("Servlet initialized.");
    }

    @Override
    protected void doGet(HttpServletRequest request, HttpServletResponse response)
            throws ServletException, IOException {

        response.setContentType("text/html");
        PrintWriter out = response.getWriter();

        String username = request.getParameter("username");
        if (username == null || username.trim().isEmpty()) {
            username = "Guest";
        }

        out.println("<html><body>");
        out.println("<h2>Welcome, " + username + "!</h2>");
        out.println("<p>This response is generated by LifecycleDemoServlet.</p>");
        out.println("</body></html>");
    }

    @Override
    public void destroy() {
        // Called once when the servlet is being removed from service
        System.out.println("Servlet destroyed.");
    }
}
